sourcesContent:
import React from "react";

const PHONE_DISPLAY = "07 45 18 65 68";
const PHONE_RAW = "0745186568";
const WHATSAPP_MESSAGE = encodeURIComponent(
  "Bonjour, je contacte BAH DÃ©bloque pour un dÃ©pannage serrurerie en urgence."
);

const scrollToId = (id) => {
  const el = document.getElementById(id);
  if (el) el.scrollIntoView({ behavior: "smooth", block: "start" });
};

function App() {
  return (
    <>
      {/* CSS DIRECT DANS APP.JS */}
      <style>{`
        * {
          box-sizing: border-box;
        }

        body {
          margin: 0;
          font-family: system-ui, -apple-system, BlinkMacSystemFont, "Segoe UI", sans-serif;
        }

        .page {
          min-height: 100vh;
          background-image: url("https://images.pexels.com/photos/279810/pexels-photo-279810.jpeg?auto=compress&cs=tinysrgb&w=1600"); /* ðŸ‘‰ change l'image si tu veux */
          background-size: cover;
          background-position: center;
          background-attachment: fixed;
          position: relative;
          color: #f9fafb;
        }

        .page::before {
          content: "";
          position: fixed;
          inset: 0;
          background: rgba(15, 23, 42, 0.75); /* voile sombre */
          z-index: 0;
        }

        .overlay {
          position: relative;
          z-index: 1;
          min-height: 100vh;
        }

        .container {
          max-width: 1100px;
          margin: 0 auto;
          padding: 20px 16px 40px;
        }

        /* HEADER */

        .topbar {
          display: flex;
          align-items: center;
          justify-content: space-between;
          gap: 16px;
          margin-bottom: 14px;
        }

        .brand {
          display: flex;
          align-items: center;
          gap: 10px;
        }

        .brand-icon {
          width: 44px;
          height: 44px;
          border-radius: 999px;
          background: rgba(15, 23, 42, 0.75);
          border: 1px solid rgba(249, 115, 22, 0.7);
          display: flex;
          align-items: center;
          justify-content: center;
          font-size: 22px;
        }

        .brand-text-main {
          font-weight: 800;
          font-size: 20px;
          letter-spacing: 0.1em;
          text-transform: uppercase;
        }

        .brand-text-sub {
          font-size: 12px;
          opacity: 0.85;
        }

        .topbar-right {
          text-align: right;
          font-size: 12px;
        }

        .topbar-phone {
          font-weight: 700;
          font-size: 16px;
        }

        .topbar-btn {
          margin-top: 6px;
          border: none;
          border-radius: 999px;
          padding: 7px 15px;
          font-size: 12px;
          background-color: #f97316;
          color: #111827;
          font-weight: 600;
          cursor: pointer;
        }

        .nav-links {
          display: flex;
          gap: 18px;
          font-size: 13px;
          color: #e5e7eb;
          margin-bottom: 14px;
        }

        .nav-link {
          cursor: pointer;
          opacity: 0.9;
        }

        .nav-link:hover {
          text-decoration: underline;
        }

        /* HERO */

        .hero {
          display: flex;
          flex-wrap: wrap;
          gap: 18px;
          margin-top: 10px;
        }

        .hero-left,
        .hero-right {
          flex: 1 1 280px;
        }

        .hero-card {
          background: rgba(15, 23, 42, 0.92);
          border-radius: 12px;
          padding: 18px 18px 16px;
          box-shadow: 0 18px 45px rgba(0, 0, 0, 0.4);
        }

        .badge {
          display: inline-flex;
          align-items: center;
          gap: 8px;
          padding: 4px 10px;
          border-radius: 999px;
          background: rgba(31, 41, 55, 0.9);
          font-size: 11px;
        }

        .badge-dot {
          width: 7px;
          height: 7px;
          border-radius: 999px;
          background-color: #22c55e;
        }

        .hero-title {
          font-size: 26px;
          line-height: 1.2;
          font-weight: 800;
          margin: 12px 0 8px 0;
        }

        .hero-title span {
          color: #f97316;
        }

