/* =========
  app.js — Locksmith site
  - Mobile menu
  - Sticky header
  - Smooth scroll
  - CTA modal (urgent call / quote)
  - Service filter
  - FAQ accordion
  - Form validation + submit
  - Simple toast
  - Image lazy-loading helper
========= */

// Helpers
const $ = (sel, scope = document) => scope.querySelector(sel);
const $$ = (sel, scope = document) => Array.from(scope.querySelectorAll(sel));

const state = {
  menuOpen: false,
  modalOpen: false,
  serviceFilter: "all",
};

// Toast
function showToast(message, type = "info") {
  let toast = $("#toast");
  if (!toast) {
    toast = document.createElement("div");
    toast.id = "toast";
    toast.style.position = "fixed";
    toast.style.left = "50%";
    toast.style.bottom = "24px";
    toast.style.transform = "translateX(-50%)";
    toast.style.padding = "12px 16px";
    toast.style.borderRadius = "8px";
    toast.style.background = type === "success" ? "#16a34a" : type === "error" ? "#dc2626" : "#334155";
    toast.style.color = "#fff";
    toast.style.fontSize = "14px";
    toast.style.boxShadow = "0 6px 24px rgba(0,0,0,0.2)";
    toast.style.zIndex = "9999";
    document.body.appendChild(toast);
  }
  toast.textContent = message;
  toast.style.display = "block";
  setTimeout(() => (toast.style.display = "none"), 2500);
}

// Mobile menu
function toggleMenu(force) {
  const nav = $("#mobileNav");
  const btn = $("#menuToggle");
  const open = typeof force === "boolean" ? force : !state.menuOpen;
  state.menuOpen = open;
  nav?.classList.toggle("open", open);
  btn?.setAttribute("aria-expanded", String(open));
  document.body.style.overflow = open ? "hidden" : "";
}

// Sticky header
function handleScrollHeader() {
  const header = $("header");
  if (!header) return;
  const scrolled = window.scrollY > 12;
  header.classList.toggle("is-sticky", scrolled);
}

// Smooth scroll for nav links
function initSmoothScroll() {
  $$('a[href^="#"]').forEach((link) => {
    link.addEventListener("click", (e) => {
      const id = link.getAttribute("href");
      const target = id && $(id);
      if (target) {
        e.preventDefault();
        target.scrollIntoView({ behavior: "smooth", block: "start" });
        toggleMenu(false);
      }
    });
  });
}

// Modal (urgent call / quote)
function openModal(id = "#ctaModal") {
  const modal = $(id);
  const overlay = $("#overlay");
  if (!modal || !overlay) return;
  modal.classList.add("open");
  overlay.classList.add("open");
  state.modalOpen = true;
  document.body.style.overflow = "hidden";
}

function closeModal(id = "#ctaModal") {
  const modal = $(id);
  const overlay = $("#overlay");
  if (!modal || !overlay) return;
  modal.classList.remove("open");
  overlay.classList.remove("open");
  state.modalOpen = false;
  document.body.style.overflow = "";
}

// Service filter (e.g., residential / commercial / emergency)
function initServiceFilter() {
  const buttons = $$(".service-filter [data-filter]");
  const items = $$(".service-card");
  if (!buttons.length || !items.length) return;

  buttons.forEach((btn) =>
    btn.addEventListener("click", () => {
      state.serviceFilter = btn.dataset.filter || "all";
      buttons.forEach((b) => b.classList.toggle("active", b === btn));
      items.forEach((item) => {
        const tags = (item.dataset.tags || "").split(",");
        const match = state.serviceFilter === "all" || tags.includes(state.serviceFilter);
        item.classList.toggle("hidden", !match);
      });
    })
  );
}

// FAQ accordion
function initFAQ() {
  $$(".faq-item").forEach((item) => {
    const q = $(".faq-question", item);
    const a = $(".faq-answer", item);
    if (!q || !a) return;
    q.addEventListener("click", () => {
      const isOpen = item.classList.contains("open");
      $$(".faq-item").forEach((el) => el.classList.remove("open"));
      if (!isOpen) item.classList.add("open");
    });
  });
}

// Form validation + submit (contact / quote)
async function initForms() {
  const form = $("#contactForm");
  if (!form) return;

  form.addEventListener("submit", async (e) => {
    e.preventDefault();

    const name = $("#name", form)?.value.trim();
    const phone = $("#phone", form)?.value.trim();
    const email = $("#email", form)?.value.trim();
    const service = $("#service", form)?.value.trim();
    const message = $("#message", form)?.value.trim();

    // Simple validations
    if (!name || (!phone && !email) || !service) {
      showToast("Merci de remplir les champs requis : nom, téléphone ou email, service.", "error");
      return;
    }
    if (phone && !/^\+?[0-9\s\-]{7,}$/.test(phone)) {
      showToast("Numéro de téléphone invalide.", "error");
      return;
    }
    if (email && !/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email)) {
      showToast("Adresse email invalide.", "error");
      return;
    }

    // Simulated submit
    const btn = $("button[type='submit']", form);
    const original = btn?.textContent;
    btn && (btn.textContent = "Envoi...");
    btn && (btn.disabled = true);

    try {
      // Replace with your real endpoint
      // const res = await fetch("/api/contact", {
      //   method: "POST",
      //   headers: { "Content-Type": "application/json" },
      //   body: JSON.stringify({ name, phone, email, service, message }),
      // });
      // if (!res.ok) throw new Error("Erreur d'envoi");
      await new Promise((r) => setTimeout(r, 900)); // demo

      showToast("Demande envoyée. Nous vous rappelons rapidement.", "success");
      form.reset();
      closeModal("#ctaModal");
    } catch (err) {
      showToast("Impossible d'envoyer la demande. Réessayez.", "error");
    } finally {
      if (btn) {
        btn.textContent = original || "Envoyer";
        btn.disabled = false;
      }
    }
  });
}

// Lazy-load utility for images with data-src
function initLazyImages() {
  const imgs = $$("img[data-src]");
  if (!imgs.length) return;

  const loadImg = (img) => {
    img.src = img.dataset.src;
    img.onload = () => img.classList.add("loaded");
  };

  if ("IntersectionObserver" in window) {
    const io = new IntersectionObserver((entries) => {
      entries.forEach((entry) => {
        if (entry.isIntersecting) {
          loadImg(entry.target);
          io.unobserve(entry.target);
        }
      });
    }, { rootMargin: "150px" });
    imgs.forEach((img) => io.observe(img));
  } else {
    imgs.forEach(loadImg);
  }
}

// Bindings
function bindUI() {
  $("#menuToggle")?.addEventListener("click", () => toggleMenu());
  $("#overlay")?.addEventListener("click", () => {
    toggleMenu(false);
    closeModal();
  });

  // CTA buttons
  $$(".js-open-modal").forEach((btn) =>
    btn.addEventListener("click", () => openModal(btn.dataset.target || "#ctaModal"))
  );
  $$(".js-close-modal").forEach((btn) =>
    btn.addEventListener("click", () => closeModal(btn.dataset.target || "#ctaModal"))
  );

  // Call now button
  $("#callNow")?.addEventListener("click", () => {
    const phone = $("#callNow")?.dataset.phone || "+33";
    window.location.href = `tel:${phone}`;
  });
}

// Init
document.addEventListener("DOMContentLoaded", () => {
  bindUI();
  initSmoothScroll();
  initServiceFilter();
  initFAQ();
  initForms();
  initLazyImages();
  handleScrollHeader();
  window.addEventListener("scroll", handleScrollHeader, { passive: true });
});
