import React from "react";

const PHONE_DISPLAY = "07 45 18 65 68";
const PHONE_RAW = "0745186568";
const EMAIL = "contact@serrurier-urgence-idf.com";

function App() {
  return (
    <>
      <style>{`
        * {
          box-sizing: border-box;
        }

        body {
          margin: 0;
          font-family: system-ui, -apple-system, BlinkMacSystemFont, "Segoe UI", sans-serif;
          color: #111827;
          background-color: #f3f4f6;
        }

        a {
          color: inherit;
        }

        .page {
          min-height: 100vh;
        }

        /* BANNIÈRE HAUT */

        .top-banner {
          background: linear-gradient(to right, #e5d0ff, #fce7f3);
          text-align: center;
          padding: 6px 8px;
          font-size: 13px;
        }

        /* HEADER */

        .header {
          background-color: #000000;
          color: #f9fafb;
          padding: 12px 32px;
          display: flex;
          align-items: center;
          justify-content: space-between;
          gap: 16px;
          position: sticky;
          top: 0;
          z-index: 40;
        }

        .header-left {
          display: flex;
          align-items: center;
          gap: 10px;
        }

        .brand-logo {
          width: 32px;
          height: 32px;
          border-radius: 999px;
          background: linear-gradient(135deg, #f97316, #facc15);
          display: flex;
          align-items: center;
          justify-content: center;
          font-size: 18px;
          color: #111827;
        }

        .brand-name {
          font-weight: 700;
          font-size: 18px;
        }

        .brand-sub {
          font-size: 11px;
          color: #d1d5db;
        }

        .nav {
          display: flex;
          gap: 20px;
          font-size: 14px;
        }

        .nav a {
          color: #f9fafb;
          text-decoration: none;
          opacity: 0.8;
        }

        .nav a:hover {
          opacity: 1;
        }

        .header-right {
          display: flex;
          flex-direction: column;
          align-items: flex-end;
          gap: 2px;
          font-size: 12px;
        }

        .header-phone {
          font-weight: 700;
          font-size: 14px;
        }

        .header-badge {
          font-size: 11px;
          padding: 2px 8px;
          border-radius: 999px;
          background-color: #111827;
          border: 1px solid #374151;
        }

        /* HERO */

        .hero {
          position: relative;
          min-height: 420px;
          color: #ffffff;
          display: flex;
          align-items: center;
          justify-content: center;
          text-align: center;
          overflow: hidden;
        }

        .hero-bg {
          position: absolute;
          inset: 0;
          background-image: url("https://images.pexels.com/photos/279810/pexels-photo-279810.jpeg?auto=compress&cs=tinysrgb&w=1600");
          background-size: cover;
          background-position: center;
          filter: brightness(0.55);
        }

        .hero-gradient {
          position: absolute;
          inset: 0;
          background: radial-gradient(circle at top, rgba(0,0,0,0.35), transparent 55%);
        }

        .hero-overlay {
          position: relative;
          max-width: 900px;
          padding: 70px 16px 60px;
        }

        .hero-kicker {
          font-size: 12px;
          letter-spacing: 0.2em;
          text-transform: uppercase;
          margin-bottom: 10px;
          opacity: 0.9;
        }

        .hero-title {
          font-size: 44px;
          font-weight: 800;
          margin-bottom: 10px;
        }

        .hero-title span {
          color: #facc15;
        }

        .hero-line {
          width: 130px;
          height: 2px;
          background-color: #ffffff;
          margin: 0 auto 16px auto;
        }

        .hero-subtitle {
          font-size: 16px;
          max-width: 640px;
          margin: 0 auto 22px auto;
          opacity: 0.95;
        }

        .hero-buttons {
          display: flex;
          flex-wrap: wrap;
          justify-content: center;
          gap: 10px;
        }

        .btn-main {
          border-radius: 999px;
          border: none;
          background-color: #f97316;
          color: #111827;
          font-weight: 700;
          padding: 10px 22px;
          font-size: 14px;
          cursor: pointer;
        }

        .btn-secondary {
          border-radius: 999px;
          border: 1px solid #e5e7eb;
          background-color: rgba(0,0,0,0.35);
          color: #f9fafb;
          font-size: 14px;
          padding: 9px 18px;
          cursor: pointer;
        }

        .hero-meta {
          margin-top: 18px;
          font-size: 12px;
          opacity: 0.9;
          display: flex;
          flex-wrap: wrap;
          justify-content: center;
          gap: 14px;
        }

        /* SECTIONS GÉNÉRALES */

        .section {
          padding: 60px 20px;
          background-color: #ffffff;
        }

        .section-alt {
          background-color: #f9fafb;
        }

        .section-narrow {
          max-width: 900px;
          margin: 0 auto;
        }

        .section-wide {
          max-width: 1100px;
          margin: 0 auto;
        }

        .section-kicker {
          font-size: 12px;
          text-transform: uppercase;
          letter-spacing: 0.18em;
          color: #6b7280;
          margin-bottom: 10px;
          text-align: center;
        }

        .section-title {
          font-size: 28px;
          font-weight: 600;
          text-align: center;
          line-height: 1.4;
          margin-bottom: 16px;
        }

        .section-sub {
          font-size: 14px;
          color: #4b5563;
          text-align: center;
          max-width: 640px;
          margin: 0 auto 24px;
        }

        /* INTERVENTIONS */

        .services-grid {
          display: grid;
          grid-template-columns: repeat(auto-fit, minmax(230px, 1fr));
          gap: 20px;
        }

        .service-card {
          border-radius: 14px;
          border: 1px solid #e5e7eb;
          background-color: #f9fafb;
          padding: 16px 16px 14px;
          font-size: 14px;
        }

        .service-header {
          display: flex;
          align-items: center;
          gap: 10px;
          margin-bottom: 6px;
        }

        .service-icon {
          width: 32px;
          height: 32px;
          border-radius: 999px;
          background-color: #111827;
          color: #f9fafb;
          display: flex;
          align-items: center;
          justify-content: center;
          font-size: 18px;
        }

        .service-title {
          font-weight: 600;
        }

        .service-text {
          color: #4b5563;
          margin-bottom: 6px;
        }

        .service-tag {
          display: inline-block;
          margin-top: 4px;
          font-size: 11px;
          padding: 2px 8px;
          border-radius: 999px;
          border: 1px solid #e5e7eb;
          color: #6b7280;
        }

        /* TARIFS */

        .tarifs-table-wrapper {
          overflow-x: auto;
          border-radius: 12px;
          border: 1px solid #e5e7eb;
          background-color: #ffffff;
        }

        .tarifs-table {
          width: 100%;
          border-collapse: collapse;
          font-size: 14px;
        }

        .tarifs-table thead {
          background-color: #f9fafb;
        }

        .tarifs-table th,
        .tarifs-table td {
          padding: 12px 14px;
        }

        .tarifs-table th {
          text-align: left;
          font-weight: 600;
        }

        .tarifs-table th:last-child,
        .tarifs-table td:last-child {
          text-align: right;
          white-space: nowrap;
        }

        .tarifs-table tbody tr:nth-child(even) td {
          background-color: #f9fafb;
        }

        .tarifs-note {
          font-size: 12px;
          color: #6b7280;
          margin-top: 10px;
          text-align: left;
        }

        .tarifs-cta {
          text-align: center;
          margin-top: 28px;
        }

        .tarifs-cta-title {
          font-size: 20px;
          font-weight: 600;
          margin-bottom: 6px;
        }

        .tarifs-cta-text {
          color: #4b5563;
          margin-bottom: 14px;
          font-size: 14px;
        }

        .btn-cta-mail {
          display: inline-block;
          background-color: #f97316;
          color: #111827;
          padding: 11px 22px;
          border-radius: 999px;
          font-weight: 700;
          text-decoration: none;
          margin-bottom: 8px;
        }

        .tarifs-cta-phone {
          font-size: 13px;
          color: #4b5563;
        }

        /* BLOC 24/7 */

        .grid-247 {
          display: grid;
          grid-template-columns: minmax(0, 1.1fr) minmax(0, 0.9fr);
          gap: 20px;
          align-items: center;
        }

        .card-247 {
          background-color: #ffffff;
          border-radius: 14px;
          border: 1px solid #e5e7eb;
          padding: 22px 18px;
        }

        .card-247-title {
          font-size: 22px;
          font-weight: 600;
          margin-bottom: 8px;
        }

        .card-247-text {
          font-size: 14px;
          color: #4b5563;
          margin-bottom: 16px;
        }

        .img-247 {
          width: 100%;
          border-radius: 14px;
          object-fit: cover;
          height: 260px;
        }

        /* AVIS */

        .avis-grid {
          display: grid;
          grid-template-columns: repeat(auto-fit, minmax(240px, 1fr));
          gap: 16px;
        }

        .avis-card {
          border-radius: 12px;
          border: 1px solid #e5e7eb;
          background-color: #f9fafb;
          padding: 12px 14px;
          font-size: 13px;
        }

        .avis-name {
          font-weight: 600;
          margin-bottom: 4px;
        }

        /* CONTACT / NEWSLETTER */

        .contact-grid {
          max-width: 900px;
          margin: 0 auto;
          display: grid;
          grid-template-columns: minmax(0, 1.1fr) minmax(0, 0.9fr);
          gap: 20px;
          align-items: center;
        }

        .contact-text-main {
          font-size: 22px;
          font-weight: 600;
          margin-bottom: 8px;
        }

        .contact-text-sub {
          font-size: 14px;
          color: #4b5563;
          margin-bottom: 10px;
        }

        .contact-details {
          font-size: 14px;
          color: #111827;
        }

        .newsletter-form {
          display: flex;
          gap: 8px;
          flex-wrap: wrap;
        }

        .newsletter-input {
          flex: 1 1 160px;
          padding: 10px 12px;
          border-radius: 999px;
          border: 1px solid #d1d5db;
          font-size: 14px;
        }

        .newsletter-btn {
          border-radius: 999px;
          border: none;
          background-color: #ec4899;
          color: white;
          font-weight: 600;
          padding: 10px 18px;
          font-size: 14px;
          cursor: pointer;
        }

        /* FOOTER */

        .footer {
          padding: 18px 24px;
          font-size: 11px;
          color: #6b7280;
          display: flex;
          justify-content: space-between;
          flex-wrap: wrap;
          gap: 8px;
          border-top: 1px solid #e5e7eb;
          background-color: #ffffff;
        }

        @media (max-width: 900px) {
          .header {
            padding: 10px 16px;
          }
          .nav {
            display: none;
          }
          .hero-title {
            font-size: 34px;
          }
          .hero-subtitle {
            font-size: 14px;
          }
          .grid-247,
          .contact-grid {
            grid-template-columns: 1fr;
          }
        }
      `}</style>

      <div className="page">
        {/* Bandeau haut */}
        <div className="top-banner">
          Serrurier urgence Paris &amp; Île-de-France • Devis rapide par téléphone ou par mail.
        </div>

        {/* HEADER */}
        <header className="header">
          <div className="header-left">
            <div className="brand-logo">🔐</div>
            <div>
              <div className="brand-name">BAH Débloque</div>
              <div className="brand-sub">Serrurier urgence · Paris &amp; IDF</div>
            </div>
          </div>

          <nav className="nav">
            <a href="#hero">Accueil</a>
            <a href="#services">Interventions</a>
            <a href="#tarifs">Tarifs</a>
            <a href="#avis">Avis</a>
            <a href="#contact">Contact</a>
          </nav>

          <div className="header-right">
            <div className="header-badge">Urgence 24h/24 – 7j/7</div>
            <div className="header-phone">{PHONE_DISPLAY}</div>
          </div>
        </header>

        {/* HERO */}
        <section id="hero" className="hero">
          <div className="hero-bg" />
          <div className="hero-gradient" />
          <div className="hero-overlay">
            <div className="hero-kicker">Serrurier professionnel</div>
            <h1 className="hero-title">
              Sécurité <span>maximisée</span> pour votre porte.
            </h1>
            <div className="hero-line" />
            <p className="hero-subtitle">
              Interventions rapides pour ouverture de porte, changement de serrure,
              blindage et sécurisation après effraction sur Paris et toute
              l&apos;Île-de-France.
            </p>
            <div className="hero-buttons">
              <button
                className="btn-main"
                onClick={() => (window.location.href = `tel:${PHONE_RAW}`)}
              >
                📞 Appeler le {PHONE_DISPLAY}
              </button>
              <button
                className="btn-secondary"
                onClick={() =>
                  (window.location.href = `mailto:${EMAIL}?subject=Demande de devis serrurerie`)
                }
              >
                ✉️ Demander un devis
              </button>
            </div>
            <div className="hero-meta">
              <span>⏱ Arrivée moyenne : ~30 minutes</span>
              <span>💶 Prix annoncés avant déplacement</span>
              <span>📄 Facture possible pour l&apos;assurance</span>
            </div>
          </div>
        </section>

        {/* INTRO */}
        <section className="section">
          <div className="section-narrow">
            <div className="section-kicker">BAH Débloque</div>
            <h2 className="section-title">
              Une équipe de serruriers expérimentés dédiée à votre tranquillité.
            </h2>
            <p className="section-sub">
              Nous intervenons chaque jour auprès de particuliers et de professionnels
              pour débloquer des portes, remplacer des serrures et renforcer la
              sécurité des logements et commerces.
            </p>
          </div>
        </section>

        {/* INTERVENTIONS */}
        <section id="services" className="section">
          <div className="section-wide">
            <h2 className="section-title">Interventions en serrurerie</h2>
            <p className="section-sub">
              Quelques exemples de situations dans lesquelles BAH Débloque intervient
              rapidement sur Paris et l&apos;Île-de-France.
            </p>

            <div className="services-grid">
              <div className="service-card">
                <div className="service-header">
                  <div className="service-icon">🚪</div>
                  <div className="service-title">Porte claquée ou bloquée</div>
                </div>
                <p className="service-text">
                  Porte fermée avec les clés à l&apos;intérieur ou serrure bloquée.
                  Ouverture propre en privilégiant la solution la moins destructive.
                </p>
                <div className="service-tag">Ouverture de porte simple</div>
              </div>

              <div className="service-card">
                <div className="service-header">
                  <div className="service-icon">🔑</div>
                  <div className="service-title">Clé cassée ou perdue</div>
                </div>
                <p className="service-text">
                  Clé cassée dans le cylindre ou perte totale du trousseau. Extraction
                  de la clé et remplacement du cylindre si nécessaire.
                </p>
                <div className="service-tag">Cylindre européen, serrures courantes</div>
              </div>

              <div className="service-card">
                <div className="service-header">
                  <div className="service-icon">🛡️</div>
                  <div className="service-title">Après effraction</div>
                </div>
                <p className="service-text">
                  Intervention après tentative de cambriolage ou effraction réussie :
                  mise en sécurité immédiate de la porte et solutions de renforcement.
                </p>
                <div className="service-tag">Sécurisation & blindage</div>
              </div>

              <div className="service-card">
                <div className="service-header">
                  <div className="service-icon">🏢</div>
                  <div className="service-title">Immeubles & locaux pros</div>
                </div>
                <p className="service-text">
                  Portes de hall, bureaux, commerces, rideaux métalliques et locaux
                  techniques. Intervention rapide pour limiter l&apos;arrêt d&apos;activité.
                </p>
                <div className="service-tag">Paris & Île-de-France</div>
              </div>
            </div>
          </div>
        </section>

        {/* TARIFS */}
        <section id="tarifs" className="section section-alt">
          <div className="section-narrow">
            <h2 className="section-title">
              Grille tarifaire (hors majoration soir / week-end)
            </h2>
            <p className="section-sub">
              Des prix clairs et transparents. Le montant exact est confirmé avant
              tout déplacement du serrurier.
            </p>

            <div className="tarifs-table-wrapper">
              <table className="tarifs-table">
                <thead>
                  <tr>
                    <th>Prestation</th>
                    <th>Prix à partir de</th>
                  </tr>
                </thead>
                <tbody>
                  <tr>
                    <td>Ouverture de porte simple claquée</td>
                    <td>90 €</td>
                  </tr>
                  <tr>
                    <td>Ouverture de porte fermée à clé</td>
                    <td>120 €</td>
                  </tr>
                  <tr>
                    <td>Ouverture de porte blindée</td>
                    <td>150 €</td>
                  </tr>
                  <tr>
                    <td>Remplacement de cylindre européen</td>
                    <td>130 €</td>
                  </tr>
                  <tr>
                    <td>Changement de serrure 1 point</td>
                    <td>140 €</td>
                  </tr>
                  <tr>
                    <td>Changement de serrure 3 points</td>
                    <td>190 €</td>
                  </tr>
                  <tr>
                    <td>Blindage de porte</td>
                    <td>450 €</td>
                  </tr>
                  <tr>
                    <td>Installation de porte blindée</td>
                    <td>Sur devis</td>
                  </tr>
                  <tr>
                    <td>Déblocage rideau métallique</td>
                    <td>180 €</td>
                  </tr>
                  <tr>
                    <td>Motorisation volet roulant</td>
                    <td>250 €</td>
                  </tr>
                  <tr>
                    <td>Installation contrôle d&apos;accès</td>
                    <td>Sur devis</td>
                  </tr>
                </tbody>
              </table>
            </div>

            <p className="tarifs-note">
              Les montants sont indicatifs et peuvent varier selon le type de porte,
              de serrure et la situation sur place. Une majoration s&apos;applique la
              nuit, les week-ends et jours fériés.
            </p>

            <div className="tarifs-cta">
              <h3 className="tarifs-cta-title">Besoin d&apos;un devis précis ?</h3>
              <p className="tarifs-cta-text">
                Décrivez votre situation (type de porte, type de serrure, photos si
                possible) et recevez une estimation gratuite en quelques minutes.
              </p>
              <a
                href={`mailto:${EMAIL}?subject=Demande de devis serrurerie&body=Bonjour,%0D%0A%0D%0AJe souhaite un devis pour : ...%0D%0AAdresse d'intervention : ...%0D%0AType de porte / serrure : ...%0D%0A%0D%0AMerci.`}
                className="btn-cta-mail"
              >
                ✉️ Demander un devis par mail
              </a>
              <div className="tarifs-cta-phone">
                Ou par téléphone au <strong>{PHONE_DISPLAY}</strong>.
              </div>
            </div>
          </div>
        </section>

        {/* 24/7 */}
        <section className="section">
          <div className="section-wide grid-247">
            <div className="card-247">
              <h3 className="card-247-title">Disponibilité 24h/24 et 7j/7</h3>
              <p className="card-247-text">
                Une urgence ne prévient pas. Nous intervenons de jour comme de nuit,
                week-ends et jours fériés. Avant chaque déplacement, nous vous
                indiquons le montant estimatif de l&apos;intervention.
              </p>
              <button
                className="btn-main"
                onClick={() => (window.location.href = `tel:${PHONE_RAW}`)}
              >
                📞 Urgence serrurerie maintenant
              </button>
            </div>
            <img
              className="img-247"
              src="https://images.pexels.com/photos/4386326/pexels-photo-4386326.jpeg?auto=compress&cs=tinysrgb&w=1200"
              alt="Serrurier en intervention"
            />
          </div>
        </section>

        {/* AVIS */}
        <section id="avis" className="section section-alt">
          <div className="section-narrow">
            <h2 className="section-title">Avis clients</h2>
            <p className="section-sub">
              Quelques retours de clients dépannés récemment par BAH Débloque. Les
              noms sont partiellement masqués par respect de leur confidentialité.
            </p>

            <div className="avis-grid">
              <div className="avis-card">
                <div className="avis-name">Sarah B***** ★★★★★</div>
                <p>
                  Porte claquée en bas de chez elle. Le serrurier est arrivé en moins
                  de 30 minutes, ouverture rapide et prix conforme à ce qui avait été
                  annoncé par téléphone.
                </p>
              </div>
              <div className="avis-card">
                <div className="avis-name">Yassine K***** ★★★★☆</div>
                <p>
                  Changement de serrure après perte de clés. Explications claires,
                  travail propre. Il garde le numéro de BAH Débloque pour la suite.
                </p>
              </div>
            </div>
          </div>
        </section>

        {/* CONTACT */}
        <section id="contact" className="section" style={{ backgroundColor: "#f4e9ff" }}>
          <div className="contact-grid">
            <div>
              <div className="contact-text-main">Contact & conseils sécurité</div>
              <p className="contact-text-sub">
                Vous avez un projet de blindage, un doute sur l&apos;état de votre
                serrure ou besoin d&apos;un devis détaillé ? Contactez-nous, nous vous
                répondons rapidement.
              </p>
              <p className="contact-details">
                Email : <strong>{EMAIL}</strong>
                <br />
                Téléphone : <strong>{PHONE_DISPLAY}</strong>
                <br />
                Zone d&apos;intervention : Paris et Île-de-France.
              </p>
            </div>
            <form
              className="newsletter-form"
              onSubmit={(e) => {
                e.preventDefault();
                alert("Formulaire fictif pour le moment 🙂 Merci de nous écrire directement par mail.");
              }}
            >
              <input
                type="email"
                placeholder="Votre adresse e-mail"
                className="newsletter-input"
              />
              <button type="submit" className="newsletter-btn">
                Envoyer
              </button>
            </form>
          </div>
        </section>

        {/* FOOTER */}
        <footer className="footer">
          <span>
            © {new Date().getFullYear()} BAH Débloque – Serrurier Paris &amp; Île-de-France
          </span>
          <span>{EMAIL}</span>
        </footer>
      </div>
    </>
  );
}

export default App;
