import React from "react";

const PHONE_DISPLAY = "07 45 18 65 68";
const PHONE_RAW = "0745186568";
const WHATSAPP_MESSAGE = encodeURIComponent(
  "Bonjour, je contacte BAH Débloque pour un dépannage serrurerie en urgence."
);

const scrollToId = (id) => {
  const el = document.getElementById(id);
  if (el) el.scrollIntoView({ behavior: "smooth", block: "start" });
};

function App() {
  return (
    <>
      {/* CSS DIRECT DANS APP.JS */}
      <style>{`
        * {
          box-sizing: border-box;
        }

        body {
          margin: 0;
          font-family: system-ui, -apple-system, BlinkMacSystemFont, "Segoe UI", sans-serif;
          background-color: #f3f4f6;
        }

        .page {
          min-height: 100vh;
          background-color: #f3f4f6;
          color: #111827;
        }

        .container {
          max-width: 1100px;
          margin: 0 auto;
          padding: 20px 16px 40px;
        }

        .topbar {
          background-color: #111827;
          color: #f9fafb;
          border-radius: 12px;
          padding: 10px 16px;
          display: flex;
          align-items: center;
          justify-content: space-between;
          gap: 12px;
        }

        .brand {
          display: flex;
          align-items: center;
          gap: 10px;
        }

        .brand-icon {
          width: 38px;
          height: 38px;
          border-radius: 999px;
          background-color: #f97316;
          display: flex;
          align-items: center;
          justify-content: center;
          font-size: 20px;
          color: #111827;
        }

        .brand-text-main {
          font-weight: 800;
          font-size: 18px;
          letter-spacing: 0.08em;
          text-transform: uppercase;
        }

        .brand-text-sub {
          font-size: 12px;
          opacity: 0.9;
        }

        .topbar-right {
          display: flex;
          flex-direction: column;
          align-items: flex-end;
          gap: 4px;
          font-size: 12px;
        }

        .topbar-phone {
          font-weight: 700;
          font-size: 15px;
        }

        .topbar-btn {
          border: none;
          border-radius: 999px;
          padding: 6px 14px;
          font-size: 12px;
          background-color: #f97316;
          color: #111827;
          font-weight: 600;
          cursor: pointer;
        }

        .nav-links {
          margin-top: 10px;
          display: flex;
          gap: 18px;
          font-size: 13px;
          color: #4b5563;
        }

        .nav-link {
          cursor: pointer;
        }

        .nav-link:hover {
          color: #111827;
        }

        .hero {
          margin-top: 22px;
          background-color: #ffffff;
          border-radius: 12px;
          padding: 22px 18px;
          display: flex;
          gap: 20px;
          flex-wrap: wrap;
          box-shadow: 0 12px 30px rgba(15, 23, 42, 0.08);
        }

        .hero-left,
        .hero-right {
          flex: 1 1 260px;
        }

        .badge {
          display: inline-flex;
          align-items: center;
          gap: 8px;
          padding: 3px 10px;
          border-radius: 999px;
          background-color: #eff6ff;
          font-size: 11px;
          color: #1d4ed8;
          margin-bottom: 10px;
        }

        .badge-dot {
          width: 7px;
          height: 7px;
          border-radius: 999px;
          background-color: #22c55e;
        }

        .hero-title {
          font-size: 26px;
          line-height: 1.25;
          font-weight: 800;
          margin-bottom: 8px;
        }

        .hero-title span {
          color: #f97316;
        }

        .hero-subtitle {
          font-size: 14px;
          color: #4b5563;
          margin-bottom: 14px;
          max-width: 520px;
        }

        .hero-cta-row {
          display: flex;
          flex-wrap: wrap;
          gap: 10px;
          margin-bottom: 10px;
        }

        .btn-main {
          border-radius: 999px;
          border: none;
          background-color: #f97316;
          color: #111827;
          font-weight: 700;
          padding: 9px 18px;
          font-size: 14px;
          cursor: pointer;
        }

        .btn-secondary {
          border-radius: 999px;
          border: 1px solid #d1d5db;
          background-color: #ffffff;
          color: #111827;
          font-size: 13px;
          padding: 8px 14px;
          cursor: pointer;
        }

        .hero-info {
          font-size: 12px;
          color: #6b7280;
          display: flex;
          flex-wrap: wrap;
          gap: 14px;
          margin-top: 6px;
        }

        .hero-right-card {
          border-radius: 10px;
          border: 1px solid #e5e7eb;
          background-color: #f9fafb;
          padding: 14px 14px 12px;
        }

        .hero-right-title {
          font-weight: 700;
          font-size: 15px;
          margin-bottom: 6px;
        }

        .hero-right-text {
          font-size: 13px;
          color: #4b5563;
          margin-bottom: 10px;
        }

        .hero-list {
          font-size: 13px;
          color: #4b5563;
          padding-left: 18px;
          margin-bottom: 10px;
        }

        .hero-price {
          font-size: 13px;
        }

        .hero-price span {
          font-weight: 700;
        }

        .section {
          margin-top: 26px;
        }

        .section-title {
          font-size: 18px;
          font-weight: 700;
          margin-bottom: 4px;
        }

        .section-sub {
          font-size: 13px;
          color: #4b5563;
          margin-bottom: 12px;
          max-width: 540px;
        }

        .grid {
          display: grid;
          grid-template-columns: repeat(auto-fit, minmax(230px, 1fr));
          gap: 12px;
        }

        .card {
          background-color: #ffffff;
          border-radius: 10px;
          border: 1px solid #e5e7eb;
          padding: 14px;
          font-size: 13px;
          color: #374151;
          box-shadow: 0 8px 20px rgba(15, 23, 42, 0.03);
        }

        .card-title {
          font-weight: 600;
          margin-bottom: 4px;
        }

        .card-icon {
          font-size: 18px;
          margin-bottom: 4px;
        }

        .card-tag {
          display: inline-block;
          margin-top: 6px;
          font-size: 11px;
          padding: 2px 7px;
          border-radius: 999px;
          border: 1px solid #e5e7eb;
          color: #4b5563;
        }

        .card ul {
          padding-left: 16px;
          margin: 4px 0 0 0;
          font-size: 13px;
          color: #4b5563;
        }

        .testimonials {
          display: grid;
          grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
          gap: 10px;
        }

        .testimonial-card {
          background-color: #ffffff;
          border-radius: 10px;
          border: 1px solid #e5e7eb;
          padding: 12px;
          font-size: 13px;
          color: #374151;
        }

        .testimonial-name {
          font-weight: 600;
          margin-bottom: 4px;
        }

        .contact-card {
          background-color: #ffffff;
          border-radius: 10px;
          border: 1px solid #e5e7eb;
          padding: 14px;
          display: grid;
          grid-template-columns: minmax(0, 1.1fr) minmax(0, 0.9fr);
          gap: 14px;
        }

        .contact-field {
          margin-bottom: 8px;
          font-size: 13px;
        }

        .contact-label {
          font-size: 11px;
          color: #6b7280;
        }

        .contact-value {
          font-weight: 500;
        }

        .contact-buttons {
          margin-top: 8px;
          display: flex;
          flex-wrap: wrap;
          gap: 8px;
        }

        .footer {
          margin-top: 22px;
          font-size: 11px;
          color: #6b7280;
          display: flex;
          justify-content: space-between;
          gap: 8px;
          flex-wrap: wrap;
          padding-top: 10px;
          border-top: 1px solid #e5e7eb;
        }

        @media (max-width: 640px) {
          .topbar {
            flex-direction: column;
            align-items: flex-start;
          }
          .topbar-right {
            align-items: flex-start;
          }
          .contact-card {
            grid-template-columns: 1fr;
          }
        }
      `}</style>

      <div className="page">
        <div className="container">
          {/* HEADER */}
          <header>
            <div className="topbar">
              <div className="brand">
                <div className="brand-icon">🔐</div>
                <div>
                  <div className="brand-text-main">BAH DÉBLOQUE</div>
                  <div className="brand-text-sub">
                    Serrurier – Paris & Île-de-France
                  </div>
                </div>
              </div>
              <div className="topbar-right">
                <div>Urgence serrurerie 24h/24 – 7j/7</div>
                <div className="topbar-phone">{PHONE_DISPLAY}</div>
                <button
                  className="topbar-btn"
                  onClick={() => (window.location.href = `tel:${PHONE_RAW}`)}
                >
                  📞 Appel immédiat
                </button>
              </div>
            </div>

            <nav className="nav-links">
              <span className="nav-link" onClick={() => scrollToId("services")}>
                Services
              </span>
              <span className="nav-link" onClick={() => scrollToId("tarifs")}>
                Tarifs
              </span>
              <span className="nav-link" onClick={() => scrollToId("avis")}>
                Avis
              </span>
              <span className="nav-link" onClick={() => scrollToId("contact")}>
                Contact
              </span>
            </nav>
          </header>

          {/* HERO */}
          <section className="hero">
            <div className="hero-left">
              <div className="badge">
                <span className="badge-dot" />
                Dépannage serrurerie Paris et alentours
              </div>
              <h1 className="hero-title">
                Porte bloquée ? <span>BAH Débloque</span> arrive.
              </h1>
              <p className="hero-subtitle">
                Ouverture de porte, clé cassée, changement de serrure et
                sécurisation après effraction. Intervention sur{" "}
                <strong>Paris</strong> et ses alentours, dans toute{" "}
                <strong>l&apos;Île-de-France</strong>.
              </p>

              <div className="hero-cta-row">
                <button
                  className="btn-main"
                  onClick={() => (window.location.href = `tel:${PHONE_RAW}`)}
                >
                  📞 Appeler le {PHONE_DISPLAY}
                </button>
                <button
                  className="btn-secondary"
                  onClick={() =>
                    (window.location.href = `https://wa.me/33${PHONE_RAW.slice(
                      1
                    )}?text=${WHATSAPP_MESSAGE}`)
                  }
                >
                  💬 Urgence WhatsApp
                </button>
              </div>

              <div className="hero-info">
                <span>⏱ Délai moyen d&apos;arrivée : ~30 min</span>
                <span>💶 Prix annoncé avant déplacement</span>
                <span>📄 Facture possible pour l&apos;assurance</span>
              </div>
            </div>

            <div className="hero-right">
              <div className="hero-right-card">
                <div className="hero-right-title">Situations les plus fréquentes</div>
                <p className="hero-right-text">
                  • Porte claquée avec les clés dedans<br />
                  • Serrure qui tourne dans le vide<br />
                  • Clé cassée dans la serrure<br />
                  • Perte ou vol de clés
                </p>
                <ul className="hero-list">
                  <li>Ouverture de porte simple ou blindée</li>
                  <li>Intervention sur Paris & Île-de-France</li>
                  <li>Devis annoncé au téléphone avant de venir</li>
                </ul>
                <div className="hero-price">
                  Ouverture de porte simple (en journée) :{" "}
                  <span>à partir de 89 €</span><br />
                  <small>(hors nuits, week-ends et jours fériés)</small>
                </div>
              </div>
            </div>
          </section>

          {/* SERVICES */}
          <section id="services" className="section">
            <h2 className="section-title">Services de serrurerie</h2>
            <p className="section-sub">
              BAH Débloque prend en charge vos urgences et vos besoins de
              serrurerie, que ce soit pour un appartement, une maison ou un local
              professionnel.
            </p>

            <div className="grid">
              <div className="card">
                <div className="card-icon">🚪</div>
                <div className="card-title">Ouverture de porte</div>
                <p>
                  Porte claquée, clé oubliée, serrure bloquée. Ouverture propre,
                  avec le moins de dégâts possible sur votre porte et votre serrure.
                </p>
                <span className="card-tag">Porte simple & blindée</span>
              </div>

              <div className="card">
                <div className="card-icon">🔑</div>
                <div className="card-title">Changement de serrure</div>
                <p>
                  Serrure endommagée, vétuste ou après une tentative d&apos;effraction.
                  Remplacement par une serrure plus fiable.
                </p>
                <span className="card-tag">Cylindres, 3 à 7 points</span>
              </div>

              <div className="card">
                <div className="card-icon">🛡️</div>
                <div className="card-title">Sécurisation</div>
                <p>
                  Pose de verrous, renforts de porte, conseils pour limiter les
                  risques de cambriolage à l&apos;avenir.
                </p>
                <span className="card-tag">Après effraction</span>
              </div>

              <div className="card">
                <div className="card-icon">🏢</div>
                <div className="card-title">Locaux & immeubles</div>
                <p>
                  Bureaux, commerces, portes de hall, caves et locaux techniques.
                  Interventions rapides pour ne pas bloquer votre activité.
                </p>
                <span className="card-tag">Paris & Île-de-France</span>
              </div>
            </div>
          </section>

          {/* TARIFS */}
          <section id="tarifs" className="section">
            <h2 className="section-title">Tarifs indicatifs</h2>
            <p className="section-sub">
              Les montants exacts dépendent de la situation (type de porte, de
              serrure, horaire). Le prix est toujours annoncé avant déplacement.
            </p>

            <div className="grid">
              <div className="card">
                <div className="card-title">En journée</div>
                <p>
                  Lundi au samedi, de 8h à 20h (hors jours fériés) sur Paris et
                  l&apos;Île-de-France.
                </p>
                <ul>
                  <li>Ouverture de porte simple : à partir de 89 €</li>
                  <li>Ouverture porte blindée : sur devis rapide</li>
                  <li>Changement de cylindre / serrure : sur devis</li>
                </ul>
              </div>

              <div className="card">
                <div className="card-title">Nuit & week-end</div>
                <p>
                  Soir, nuit, dimanches et jours fériés. Une majoration s&apos;applique,
                  expliquée avant toute intervention.
                </p>
                <ul>
                  <li>Majoration nuit / week-end</li>
                  <li>Vous décidez avant que l&apos;on se déplace</li>
                </ul>
              </div>

              <div className="card">
                <div className="card-title">Assurances</div>
                <p>
                  En cas d&apos;effraction, tentative de cambriolage ou perte de clés,
                  une partie des frais peut être prise en charge par votre assurance.
                </p>
                <ul>
                  <li>Facture claire et détaillée</li>
                  <li>Accompagnement pour vos démarches</li>
                </ul>
              </div>
            </div>
          </section>

          {/* AVIS – PEU, AVEC PRÉNOM + ÉTOILES */}
          <section id="avis" className="section">
            <h2 className="section-title">Avis clients</h2>
            <p className="section-sub">
              Quelques personnes dépannées par BAH Débloque sur Paris et ses
              alentours :
            </p>

            <div className="testimonials">
              <div className="testimonial-card">
                <div className="testimonial-name">Sarah ★★★★★</div>
                <p>
                  Porte claquée en bas de chez elle, intervention rapide et prix
                  conforme à ce qui avait été annoncé. Elle recommande.
                </p>
              </div>

              <div className="testimonial-card">
                <div className="testimonial-name">Yassine ★★★★☆</div>
                <p>
                  Changement de serrure après perte de clés. Explications claires,
                  travail propre, il garde le numéro pour la prochaine fois.
                </p>
              </div>
            </div>
          </section>

          {/* CONTACT */}
          <section id="contact" className="section">
            <h2 className="section-title">Contact BAH Débloque</h2>
            <p className="section-sub">
              Pour une urgence ou une question sur un futur changement de serrure,
              tu peux appeler ou envoyer un message directement.
            </p>

            <div className="contact-card">
              <div>
                <div className="contact-field">
                  <div className="contact-label">Téléphone (urgence)</div>
                  <div className="contact-value">{PHONE_DISPLAY}</div>
                </div>
                <div className="contact-field">
                  <div className="contact-label">WhatsApp</div>
                  <div className="contact-value">
                    +33 {PHONE_RAW.slice(1, 3)} {PHONE_RAW.slice(3, 5)}{" "}
                    {PHONE_RAW.slice(5, 7)} {PHONE_RAW.slice(7)}
                  </div>
                </div>
                <div className="contact-field">
                  <div className="contact-label">Email</div>
                  <div className="contact-value">contact@bah-debloque.fr</div>
                </div>

                <div className="contact-buttons">
                  <button
                    className="btn-main"
                    onClick={() => (window.location.href = `tel:${PHONE_RAW}`)}
                  >
                    📞 Appel immédiat
                  </button>
                  <button
                    className="btn-secondary"
                    onClick={() =>
                      (window.location.href = `https://wa.me/33${PHONE_RAW.slice(
                        1
                      )}?text=${WHATSAPP_MESSAGE}`)
                    }
                  >
                    💬 Écrire sur WhatsApp
                  </button>
                </div>
              </div>

              <div>
                <div className="contact-field">
                  <div className="contact-label">Zone d&apos;intervention</div>
                  <div className="contact-value">
                    Paris et ses alentours, dans toute l&apos;Île-de-France.
                  </div>
                </div>
                <div className="contact-field">
                  <div className="contact-label">Horaires</div>
                  <div className="contact-value">
                    Possibilité d&apos;intervention 24h/24 – 7j/7 (tarifs différents la
                    nuit et le week-end).
                  </div>
                </div>
                <div className="contact-field">
                  <div className="contact-label">Nom commercial</div>
                  <div className="contact-value">
                    BAH DÉBLOQUE – Serrurier dépanneur
                  </div>
                </div>
              </div>
            </div>
          </section>

          {/* FOOTER */}
          <footer className="footer">
            <span>
              © {new Date().getFullYear()} BAH Débloque – Serrurier Paris & Île-de-France
            </span>
            <span>Mentions légales · Politique de confidentialité (à compléter)</span>
          </footer>
        </div>
      </div>
    </>
  );
}

export default App;
