import React from "react";

const PHONE_DISPLAY = "07 45 18 65 68";
const PHONE_RAW = "0745186568";
const EMAIL = "contact@serrurier-urgence-idf.com";

function App() {
  const handleDevisSubmit = (event) => {
    event.preventDefault();
    const formData = new FormData(event.target);

    const nom = formData.get("nom") || "";
    const prenom = formData.get("prenom") || "";
    const telephone = formData.get("telephone") || "";
    const email = formData.get("email") || "";
    const message = formData.get("message") || "";

    const body = `
Nom : ${nom}
Prénom : ${prenom}
Téléphone : ${telephone}
Email : ${email}

Message :
${message}
    `.trim();

    const mailto = `mailto:${EMAIL}?subject=${encodeURIComponent(
      "Demande de devis serrurerie"
    )}&body=${encodeURIComponent(body)}`;

    window.location.href = mailto;
  };

  return (
    <>
      <style>{`
        * {
          box-sizing: border-box;
        }

        body {
          margin: 0;
          font-family: system-ui, -apple-system, BlinkMacSystemFont, "Segoe UI", sans-serif;
          color: #111827;
          background-color: #f3f4f6;
        }

        a {
          color: inherit;
        }

        .page {
          min-height: 100vh;
        }

        /* BANNIÈRE HAUT */

        .top-banner {
          background: linear-gradient(to right, #e5d0ff, #fce7f3);
          text-align: center;
          padding: 6px 8px;
          font-size: 13px;
        }

        /* HEADER */

        .header {
          background-color: #000000;
          color: #f9fafb;
          padding: 12px 32px;
          display: flex;
          align-items: center;
          justify-content: space-between;
          gap: 16px;
          position: sticky;
          top: 0;
          z-index: 40;
        }

        .header-left {
          display: flex;
          align-items: center;
          gap: 10px;
        }

        .brand-logo-img {
          height: 34px;
          width: auto;
          display: block;
        }

        .brand-name {
          font-weight: 700;
          font-size: 18px;
        }

        .brand-sub {
          font-size: 11px;
          color: #d1d5db;
        }

        .nav {
          display: flex;
          gap: 20px;
          font-size: 14px;
        }

        .nav a {
          color: #f9fafb;
          text-decoration: none;
          opacity: 0.8;
        }

        .nav a:hover {
          opacity: 1;
        }

        .header-right {
          display: flex;
          flex-direction: column;
          align-items: flex-end;
          gap: 2px;
          font-size: 12px;
        }

        .header-phone {
          font-weight: 700;
          font-size: 14px;
        }

        .header-badge {
          font-size: 11px;
          padding: 2px 8px;
          border-radius: 999px;
          background-color: #111827;
          border: 1px solid #374151;
        }

        /* HERO */

        .hero {
          position: relative;
          min-height: 420px;
          color: #ffffff;
          display: flex;
          align-items: center;
          justify-content: center;
          text-align: center;
          overflow: hidden;
        }

        .hero-bg {
          position: absolute;
          inset: 0;
          background-image: url("https://images.pexels.com/photos/279810/pexels-photo-279810.jpeg?auto=compress&cs=tinysrgb&w=1600");
          background-size: cover;
          background-position: center;
          filter: brightness(0.55);
        }

        .hero-gradient {
          position: absolute;
          inset: 0;
          background: radial-gradient(circle at top, rgba(0,0,0,0.35), transparent 55%);
        }

        .hero-overlay {
          position: relative;
          max-width: 900px;
          padding: 70px 16px 60px;
        }

        .hero-kicker {
          font-size: 12px;
          letter-spacing: 0.2em;
          text-transform: uppercase;
          margin-bottom: 10px;
          opacity: 0.9;
        }

        .hero-title {
          font-size: 40px;
          font-weight: 800;
          margin-bottom: 10px;
        }

        .hero-title span {
          color: #facc15;
        }

        .hero-line {
          width: 130px;
          height: 2px;
          background-color: #ffffff;
          margin: 0 auto 16px auto;
        }

        .hero-subtitle {
          font-size: 16px;
          max-width: 640px;
          margin: 0 auto 22px auto;
          opacity: 0.95;
        }

        .hero-buttons {
          display: flex;
          flex-wrap: wrap;
          justify-content: center;
          gap: 10px;
        }

        .btn-main {
          border-radius: 999px;
          border: none;
          background-color: #f97316;
          color: #111827;
          font-weight: 700;
          padding: 10px 22px;
          font-size: 14px;
          cursor: pointer;
        }

        .btn-secondary {
          border-radius: 999px;
          border: 1px solid #e5e7eb;
          background-color: rgba(0,0,0,0.35);
          color: #f9fafb;
          font-size: 14px;
          padding: 9px 18px;
          cursor: pointer;
        }

        .hero-meta {
          margin-top: 18px;
          font-size: 12px;
          opacity: 0.9;
          display: flex;
          flex-wrap: wrap;
          justify-content: center;
          gap: 14px;
        }

        /* SECTIONS GÉNÉRALES */

        .section {
          padding: 60px 20px;
          background-color: #ffffff;
        }

        .section-alt {
          background-color: #f9fafb;
        }

        .section-narrow {
          max-width: 900px;
          margin: 0 auto;
        }

        .section-wide {
          max-width: 1100px;
          margin: 0 auto;
        }

        .section-title {
          font-size: 28px;
          font-weight: 600;
          text-align: center;
          line-height: 1.4;
          margin-bottom: 16px;
        }

        .section-sub {
          font-size: 14px;
          color: #4b5563;
          text-align: center;
          max-width: 640px;
          margin: 0 auto 24px;
        }

        /* INTERVENTIONS */

        .services-grid {
          display: grid;
          grid-template-columns: repeat(auto-fit, minmax(230px, 1fr));
          gap: 20px;
        }

        .service-card {
          border-radius: 14px;
          border: 1px solid #e5e7eb;
          background-color: #f9fafb;
          padding: 16px 16px 14px;
          font-size: 14px;
        }

        .service-header {
          display: flex;
          align-items: center;
          gap: 10px;
          margin-bottom: 6px;
        }

        .service-icon {
          width: 32px;
          height: 32px;
          border-radius: 999px;
          background-color: #111827;
          color: #f9fafb;
          display: flex;
          align-items: center;
          justify-content: center;
          font-size: 18px;
        }

        .service-title {
          font-weight: 600;
        }

        .service-text {
          color: #4b5563;
          margin-bottom: 6px;
        }

        .service-tag {
          display: inline-block;
          margin-top: 4px;
          font-size: 11px;
          padding: 2px 8px;
          border-radius: 999px;
          border: 1px solid #e5e7eb;
          color: #6b7280;
        }

        /* TARIFS */

        .tarifs-table-wrapper {
          overflow-x: auto;
          border-radius: 12px;
          border: 1px solid #e5e7eb;
          background-color: #ffffff;
        }

        .tarifs-table {
          width: 100%;
          border-collapse: collapse;
          font-size: 14px;
        }

        .tarifs-table thead {
          background-color: #f9fafb;
        }

        .tarifs-table th,
        .tarifs-table td {
          padding: 12px 14px;
        }

        .tarifs-table th {
          text-align: left;
          font-weight: 600;
        }

        .tarifs-table th:last-child,
        .tarifs-table td:last-child {
          text-align: right;
          white-space: nowrap;
        }

        .tarifs-table tbody tr:nth-child(even) td {
          background-color: #f9fafb;
        }

        .tarifs-note {
          font-size: 12px;
          color: #6b7280;
          margin-top: 10px;
          text-align: left;
        }

        .tarifs-cta {
          text-align: center;
          margin-top: 28px;
        }

        .tarifs-cta-title {
          font-size: 20px;
          font-weight: 600;
          margin-bottom: 6px;
        }

        .tarifs-cta-text {
          color: #4b5563;
          margin-bottom: 14px;
          font-size: 14px;
        }

        .btn-cta-mail {
          display: inline-block;
          background-color: #f97316;
          color: #111827;
          padding: 11px 22px;
          border-radius: 999px;
          font-weight: 700;
          text-decoration: none;
          margin-bottom: 8px;
        }

        .tarifs-cta-phone {
          font-size: 13px;
          color: #4b5563;
        }

        /* BLOC 24/7 */

        .grid-247 {
          display: grid;
          grid-template-columns: minmax(0, 1.1fr) minmax(0, 0.9fr);
          gap: 20px;
          align-items: center;
        }

        .card-247 {
          background-color: #ffffff;
          border-radius: 14px;
          border: 1px solid #e5e7eb;
          padding: 22px 18px;
        }

        .card-247-title {
          font-size: 22px;
          font-weight: 600;
          margin-bottom: 8px;
        }

        .card-247-text {
          font-size: 14px;
          color: #4b5563;
          margin-bottom: 16px;
        }

        .img-247 {
          width: 100%;
          border-radius: 14px;
          object-fit: cover;
          height: 260px;
        }

        /* AVIS */

        .avis-grid {
          display: grid;
          grid-template-columns: repeat(auto-fit, minmax(240px, 1fr));
          gap: 16px;
        }

        .avis-card {
          border-radius: 12px;
          border: 1px solid #e5e7eb;
          background-color: #f9fafb;
          padding: 12px 14px;
          font-size: 13px;
        }

        .avis-name {
          font-weight: 600;
          margin-bottom: 4px;
        }

        /* FORMULAIRE DEVIS */

        .contact-section {
          background-color: #f4e9ff;
          padding: 60px 20px;
        }

        .contact-grid {
          max-width: 900px;
          margin: 0 auto;
          display: grid;
          grid-template-columns: minmax(0, 1.1fr) minmax(0, 0.9fr);
          gap: 20px;
          align-items: flex-start;
        }

        .contact-title {
          font-size: 24px;
          font-weight: 600;
          margin-bottom: 8px;
        }

        .contact-text {
          font-size: 14px;
          color: #4b5563;
          margin-bottom: 10px;
        }

        .contact-details {
          font-size: 14px;
          color: #111827;
        }

        .devis-form {
          background-color: #ffffff;
          border-radius: 14px;
          border: 1px solid #e5e7eb;
          padding: 18px 16px;
          font-size: 14px;
        }

        .form-row {
          display: flex;
          gap: 10px;
          flex-wrap: wrap;
        }

        .form-group {
          margin-bottom: 10px;
          flex: 1 1 140px;
        }

        .form-label {
          font-size: 12px;
          color: #4b5563;
          margin-bottom: 4px;
          display: block;
        }

        .form-input,
        .form-textarea {
          width: 100%;
          padding: 8px 10px;
          border-radius: 8px;
          border: 1px solid #d1d5db;
          font-size: 14px;
          font-family: inherit;
        }

        .form-textarea {
          min-height: 90px;
          resize: vertical;
        }

        .form-submit {
          margin-top: 8px;
          display: inline-block;
          border-radius: 999px;
          border: none;
          background-color: #f97316;
          color: #111827;
          font-weight: 700;
          padding: 9px 18px;
          font-size: 14px;
          cursor: pointer;
        }

        .form-note {
          margin-top: 6px;
          font-size: 11px;
          color: #6b7280;
        }

        /* FOOTER */

        .footer {
          padding: 18px 24px;
          font-size: 11px;
          color: #6b7280;
          display: flex;
          justify-content: space-between;
          flex-wrap: wrap;
          gap: 8px;
          border-top: 1px solid #e5e7eb;
          background-color: #ffffff;
        }

        @media (max-width: 900px) {
          .header {
            padding: 10px 16px;
          }
          .nav {
            display: none;
          }
          .hero-title {
            font-size: 32px;
          }
          .hero-subtitle {
            font-size: 14px;
          }
          .grid-247,
          .contact-grid {
            grid-template-columns: 1fr;
          }
        }
      `}</style>

      <div className="page">
        {/* Bandeau haut */}
        <div className="top-banner">
          Serrurier urgence Paris &amp; Île-de-France • Devis rapide par téléphone ou par mail.
        </div>

        {/* HEADER */}
        <header className="header">
          <div className="header-left">
            <img
              src="/logo-bah-debloque.png"
              alt="BAH Débloque"
              className="brand-logo-img"
            />
            <div>
              <div className="brand-name">BAH Débloque</div>
              <div className="brand-sub">Serrurier urgence · Paris &amp; IDF</div>
            </div>
          </div>

          <nav className="nav">
            <a href="#hero">Accueil</a>
            <a href="#services">Interventions</a>
            <a href="#tarifs">Tarifs</a>
            <a href="#avis">Avis</a>
            <a href="#contact">Devis</a>
          </nav>

          <div className="header-right">
            <div className="header-badge">Urgence 24h/24 – 7j/7</div>
            <div className="header-phone">{PHONE_DISPLAY}</div>
          </div>
        </header>

        {/* HERO */}
        <section id="hero" className="hero">
          <div className="hero-bg" />
          <div className="hero-gradient" />
          <div className="hero-overlay">
            <div className="hero-kicker">Serrurier urgence</div>
            <h1 className="hero-title">
              Porte bloquée ? <span>BAH Débloque</span> intervient.
            </h1>
            <div className="hero-line" />
            <p className="hero-subtitle">
              Porte claquée, clé cassée ou serrure qui ne tourne plus : nous intervenons
              rapidement sur Paris et ses alentours pour vous remettre chez vous sans
              stress.
            </p>
            <div className="hero-buttons">
              <button
                className="btn-main"
                onClick={() => (window.location.href = `tel:${PHONE_RAW}`)}
              >
                📞 Appeler le {PHONE_DISPLAY}
              </button>
              <button
                className="btn-secondary"
                onClick={() =>
                  document.getElementById("contact")?.scrollIntoView({ behavior: "smooth" })
                }
              >
                ✉️ Faire une demande de devis
              </button>
            </div>
            <div className="hero-meta">
              <span>⏱ Arrivée moyenne : ~30 minutes</span>
              <span>💶 Prix annoncés avant déplacement</span>
              <span>📄 Facture possible pour l&apos;assurance</span>
            </div>
          </div>
        </section>

        {/* INTERVENTIONS */}
        <section id="services" className="section">
          <div className="section-wide">
            <h2 className="section-title">Interventions en serrurerie</h2>
            <p className="section-sub">
              Quelques exemples de situations dans lesquelles BAH Débloque intervient
              au quotidien à Paris et en Île-de-France.
            </p>

            <div className="services-grid">
              <div className="service-card">
                <div className="service-header">
                  <div className="service-icon">🚪</div>
                  <div className="service-title">Porte claquée ou bloquée</div>
                </div>
                <p className="service-text">
                  Porte fermée avec les clés à l&apos;intérieur ou serrure coincée.
                  Ouverture propre en privilégiant la solution la moins destructive.
                </p>
                <div className="service-tag">Ouverture de porte simple</div>
              </div>

              <div className="service-card">
                <div className="service-header">
                  <div className="service-icon">🔑</div>
                  <div className="service-title">Clé cassée ou perdue</div>
                </div>
                <p className="service-text">
                  Clé cassée dans le cylindre ou trousseau introuvable. Extraction
                  et remplacement du cylindre si nécessaire.
                </p>
                <div className="service-tag">Cylindre européen, serrures courantes</div>
              </div>

              <div className="service-card">
                <div className="service-header">
                  <div className="service-icon">🛡️</div>
                  <div className="service-title">Après tentative d&apos;effraction</div>
                </div>
                <p className="service-text">
                  Serrure forcée, porte abîmée : mise en sécurité rapide du logement
                  et solutions adaptées à votre budget.
                </p>
                <div className="service-tag">Sécurisation urgente</div>
              </div>

              <div className="service-card">
                <div className="service-header">
                  <div className="service-icon">🏢</div>
                  <div className="service-title">Immeubles & locaux pros</div>
                </div>
                <p className="service-text">
                  Portes de hall, bureaux, locaux techniques, rideaux métalliques.
                  Intervention rapide pour ne pas bloquer votre activité.
                </p>
                <div className="service-tag">Paris & Île-de-France</div>
              </div>
            </div>
          </div>
        </section>

        {/* TARIFS */}
        <section id="tarifs" className="section section-alt">
          <div className="section-narrow">
            <h2 className="section-title">
              Grille tarifaire (hors majoration soir / week-end)
            </h2>
            <p className="section-sub">
              Des prix clairs et transparents. Le montant exact est confirmé avant
              tout déplacement.
            </p>

            <div className="tarifs-table-wrapper">
              <table className="tarifs-table">
                <thead>
                  <tr>
                    <th>Prestation</th>
                    <th>Prix à partir de</th>
                  </tr>
                </thead>
                <tbody>
                  <tr>
                    <td>Ouverture de porte simple claquée</td>
                    <td>90 €</td>
                  </tr>
                  <tr>
                    <td>Ouverture de porte fermée à clé</td>
                    <td>120 €</td>
                  </tr>
                  <tr>
                    <td>Ouverture de porte blindée</td>
                    <td>150 €</td>
                  </tr>
                  <tr>
                    <td>Remplacement de cylindre européen</td>
                    <td>130 €</td>
                  </tr>
                  <tr>
                    <td>Changement de serrure 1 point</td>
                    <td>140 €</td>
                  </tr>
                  <tr>
                    <td>Changement de serrure 3 points</td>
                    <td>190 €</td>
                  </tr>
                  <tr>
                    <td>Blindage de porte</td>
                    <td>450 €</td>
                  </tr>
                  <tr>
                    <td>Installation de porte blindée</td>
                    <td>Sur devis</td>
                  </tr>
                  <tr>
                    <td>Déblocage rideau métallique</td>
                    <td>180 €</td>
                  </tr>
                  <tr>
                    <td>Motorisation volet roulant</td>
                    <td>250 €</td>
                  </tr>
                  <tr>
                    <td>Installation contrôle d&apos;accès</td>
                    <td>Sur devis</td>
                  </tr>
                </tbody>
              </table>
            </div>

            <p className="tarifs-note">
              Les montants sont indicatifs et peuvent varier selon le type de porte,
              de serrure et la situation sur place. Une majoration s&apos;applique la
              nuit, les week-ends et jours fériés.
            </p>

            <div className="tarifs-cta">
              <h3 className="tarifs-cta-title">Vous voulez connaître le prix exact ?</h3>
              <p className="tarifs-cta-text">
                Expliquez votre situation ou envoyez des photos et on vous répond avec
                une estimation claire avant toute intervention.
              </p>
              <a
                href={`mailto:${EMAIL}?subject=Demande de devis serrurerie`}
                className="btn-cta-mail"
              >
                ✉️ Demander un devis par mail
              </a>
              <div className="tarifs-cta-phone">
                Ou par téléphone au <strong>{PHONE_DISPLAY}</strong>.
              </div>
            </div>
          </div>
        </section>

        {/* 24/7 */}
        <section className="section">
          <div className="section-wide grid-247">
            <div className="card-247">
              <h3 className="card-247-title">Disponibilité 24h/24 et 7j/7</h3>
              <p className="card-247-text">
                Une porte qui ne s&apos;ouvre plus, ça n&apos;attend pas le lendemain.
                J&apos;interviens de jour comme de nuit, week-ends et jours fériés,
                avec un tarif annoncé avant le déplacement.
              </p>
              <button
                className="btn-main"
                onClick={() => (window.location.href = `tel:${PHONE_RAW}`)}
              >
                📞 Urgence serrurerie maintenant
              </button>
            </div>
            <img
              className="img-247"
              src="https://images.pexels.com/photos/8092/door-locksmith-tools.jpeg?auto=compress&cs=tinysrgb&w=1200"
              alt="Serrurier en intervention"
            />
          </div>
        </section>

        {/* AVIS */}
        <section id="avis" className="section section-alt">
          <div className="section-narrow">
            <h2 className="section-title">Avis clients</h2>
            <p className="section-sub">
              Quelques retours de personnes dépannées par BAH Débloque. Noms partiels
              pour garder leur confidentialité.
            </p>

            <div className="avis-grid">
              <div className="avis-card">
                <div className="avis-name">Sarah B***** ★★★★★</div>
                <p>
                  Je suis restée bloquée dehors en rentrant du travail, porte claquée
                  avec les clés à l&apos;intérieur. Il est arrivé en une trentaine de
                  minutes, a ouvert la porte rapidement et le prix était le même que
                  celui annoncé au téléphone.
                </p>
              </div>
              <div className="avis-card">
                <div className="avis-name">Yassine K***** ★★★★☆</div>
                <p>
                  J&apos;avais perdu mes clés en pleine nuit. Il m&apos;a ouvert la
                  porte puis proposé de changer le cylindre. Il a tout expliqué étape
                  par étape, travail propre. Je garde le numéro.
                </p>
              </div>
              <div className="avis-card">
                <div className="avis-name">Nadia L***** ★★★★★</div>
                <p>
                  Suite à une tentative d&apos;effraction sur ma porte, il est venu
                  sécuriser rapidement en posant une solution provisoire puis un
                  renfort plus solide. Très rassurant dans un moment comme ça.
                </p>
              </div>
            </div>
          </div>
        </section>

        {/* FORMULAIRE DEVIS */}
        <section id="contact" className="contact-section">
          <div className="contact-grid">
            <div>
              <div className="contact-title">Faire une demande de devis</div>
              <p className="contact-text">
                Explique ton problème (porte claquée, clé cassée, serrure qui tourne dans
                le vide, rideau métallique, etc.) et tu reçois une réponse rapidement.
              </p>
              <p className="contact-details">
                Email : <strong>{EMAIL}</strong>
                <br />
                Téléphone : <strong>{PHONE_DISPLAY}</strong>
                <br />
                Zone d&apos;intervention : Paris et Île-de-France.
              </p>
            </div>

            <form className="devis-form" onSubmit={handleDevisSubmit}>
              <div className="form-row">
                <div className="form-group">
                  <label className="form-label" htmlFor="nom">
                    Nom
                  </label>
                  <input
                    id="nom"
                    name="nom"
                    type="text"
                    className="form-input"
                    placeholder="Votre nom"
                    required
                  />
                </div>
                <div className="form-group">
                  <label className="form-label" htmlFor="prenom">
                    Prénom
                  </label>
                  <input
                    id="prenom"
                    name="prenom"
                    type="text"
                    className="form-input"
                    placeholder="Votre prénom"
                    required
                  />
                </div>
              </div>

              <div className="form-row">
                <div className="form-group">
                  <label className="form-label" htmlFor="telephone">
                    Téléphone
                  </label>
                  <input
                    id="telephone"
                    name="telephone"
                    type="tel"
                    className="form-input"
                    placeholder="07..."
                    required
                  />
                </div>
                <div className="form-group">
                  <label className="form-label" htmlFor="email">
                    Email
                  </label>
                  <input
                    id="email"
                    name="email"
                    type="email"
                    className="form-input"
                    placeholder="vous@mail.com"
                    required
                  />
                </div>
              </div>

              <div className="form-group">
                <label className="form-label" htmlFor="message">
                  Message
                </label>
                <textarea
                  id="message"
                  name="message"
                  className="form-textarea"
                  placeholder="Décris ton problème (type de porte, type de serrure, ce qui s'est passé)..."
                  required
                />
              </div>

              <button type="submit" className="form-submit">
                ✉️ Envoyer ma demande de devis
              </button>
              <div className="form-note">
                Tu peux aussi envoyer directement un mail à {EMAIL} en joignant des photos
                si besoin.
              </div>
            </form>
          </div>
        </section>

        {/* FOOTER */}
        <footer className="footer">
          <span>
            © {new Date().getFullYear()} BAH Débloque – Serrurier Paris &amp; Île-de-France
          </span>
          <span>{EMAIL}</span>
        </footer>
      </div>
    </>
  );
}

export default App;
