import { useState, useEffect } from "react";
import "./App.css";
import axios from "axios";
import {
  Phone,
  Lock,
  Shield,
  Star,
  Clock,
  Award,
  CheckCircle,
  Key,
  Home,
  AlertCircle,
  ChevronDown,
} from "lucide-react";

const BACKEND_URL = process.env.REACT_APP_BACKEND_URL;
const API = `${BACKEND_URL}/api`;

function App() {
  const [formData, setFormData] = useState({
    name: "",
    phone: "",
    email: "",
    message: "",
  });
  const [openFaq, setOpenFaq] = useState(null);
  const [testimonials, setTestimonials] = useState([]);
  const [submitStatus, setSubmitStatus] = useState(null);

  useEffect(() => {
    fetchTestimonials();
  }, []);

  const fetchTestimonials = async () => {
    try {
      const response = await axios.get(`${API}/testimonials`);
      setTestimonials(response.data);
    } catch (error) {
      console.error("Error fetching testimonials:", error);
    }
  };

  const handleInputChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post(`${API}/contact`, formData);
      setSubmitStatus("success");
      setFormData({ name: "", phone: "", email: "", message: "" });
      setTimeout(() => setSubmitStatus(null), 5000);
    } catch (error) {
      console.error("Error submitting form:", error);
      setSubmitStatus("error");
      setTimeout(() => setSubmitStatus(null), 5000);
    }
  };

  const scrollToContact = () => {
    document
      .getElementById("contact")
      ?.scrollIntoView({ behavior: "smooth" });
  };

  const faqItems = [
    {
      question: "Quel est votre délai d'intervention ?",
      answer:
        "Nous nous engageons à intervenir en moins de 40 minutes à Paris et dans les régions proches. Pour les zones plus éloignées, nous vous communiquons un délai précis lors de votre appel.",
    },
    {
      question: "Pratiquez-vous des tarifs de nuit majorés ?",
      answer:
        "Nos tarifs sont transparents et communiqués avant intervention. Des frais supplémentaires peuvent s'appliquer pour les interventions de nuit, week-end et jours fériés. Le devis gratuit vous sera communiqué par téléphone.",
    },
    {
      question: "Quels moyens de paiement acceptez-vous ?",
      answer:
        "Nous acceptons les paiements par carte bancaire, espèces et chèque. Un reçu détaillé vous sera remis à la fin de l'intervention.",
    },
    {
      question: "Êtes-vous assuré et certifié ?",
      answer:
        "Oui, nous sommes une entreprise certifiée et assurée. Tous nos techniciens sont qualifiés et notre matériel est certifié. Nous offrons une garantie de 2 ans sur toutes nos prestations.",
    },
  ];

  return (
    <div className="min-h-screen bg-white">
      {/* Header */}
      <header className="bg-white shadow-sm fixed w-full top-0 z-50">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-4 flex justify-between items-center">
          <div className="flex items-center space-x-3">
            <div className="bg-blue-900 p-2 rounded-lg">
              <Lock className="h-6 w-6 text-white" />
            </div>
            <div>
              <h1 className="text-xl font-bold text-gray-900">BAH Débloque</h1>
              <p className="text-xs text-gray-600">Serrurier professionnel</p>
            </div>
          </div>
          <a
            href="tel:0614795157"
            className="bg-orange-500 hover:bg-orange-600 text-white px-6 py-3 rounded-lg font-semibold flex items-center space-x-2 transition-colors"
          >
            <Phone className="h-5 w-5" />
            <span>Urgence 24/7</span>
          </a>
        </div>
      </header>

      {/* Hero */}
      <section className="bg-gradient-to-br from-blue-900 via-blue-800 to-blue-900 text-white pt-32 pb-20">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 text-center">
          <div className="inline-flex items-center space-x-2 bg-orange-500 px-4 py-2 rounded-full mb-8">
            <Clock className="h-5 w-5" />
            <span className="font-semibold">
              Intervention rapide sous 40 minutes
            </span>
          </div>

          <h2 className="text-5xl md:text-6xl font-bold mb-4">
            Votre serrurier de confiance
          </h2>
          <p className="text-4xl md:text-5xl font-bold text-orange-500 mb-8">
            24h/24 - 7j/7
          </p>

          <p className="text-xl text-blue-100 mb-12 max-w-3xl mx-auto">
            Porte claquée ? Serrure bloquée ? Nous intervenons rapidement à
            Paris et dans toutes les régions alentours pour tous vos dépannages
            et installations.
          </p>

          <div className="flex flex-col sm:flex-row gap-4 justify-center mb-16">
            <a
              href="tel:0614795157"
              className="bg-orange-500 hover:bg-orange-600 text-white px-8 py-4 rounded-lg font-bold text-lg flex items-center justify-center space-x-2 transition-colors"
            >
              <Phone className="h-5 w-5" />
              <span>Appeler maintenant</span>
            </a>
            <button
              onClick={scrollToContact}
              className="border-2 border-white hover:bg-white hover:text-blue-900 text-white px-8 py-4 rounded-lg font-bold text-lg transition-colors"
            >
              Demander un devis
            </button>
          </div>

          <div className="flex flex-col sm:flex-row gap-8 justify-center items-center text-center">
            <div className="flex items-center space-x-2">
              <Shield className="h-6 w-6 text-orange-500" />
              <span className="font-semibold">Serrurier assuré</span>
            </div>
            <div className="flex items-center space-x-2">
              <Star className="h-6 w-6 text-orange-500" />
              <span className="font-semibold">+500 interventions</span>
            </div>
            <div className="flex items-center space-x-2">
              <Clock className="h-6 w-6 text-orange-500" />
              <span className="font-semibold">Devis gratuit</span>
            </div>
          </div>
        </div>
      </section>

      {/* Services */}
      <section className="py-20 bg-gray-50">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="text-center mb-16">
            <h2 className="text-4xl font-bold text-gray-900 mb-4">
              Nos services
            </h2>
            <p className="text-xl text-gray-600">
              Des solutions pour tous vos besoins
            </p>
            <p className="text-gray-500 mt-2">
              Expertise et professionnalisme au service de votre sécurité
            </p>
          </div>

          <div className="grid md:grid-cols-2 lg:grid-cols-4 gap-8">
            {/* 4 cartes services */}
            {/* ... (je te laisse tel quel, c’est exactement ton code Emergent) */}
          </div>
        </div>
      </section>

      {/* Les autres sections (Pourquoi nous choisir, Zones d’intervention,
          Témoignages, FAQ, Contact, Footer) restent exactement comme
          dans ton App.js actuel. Pour ne pas exploser le message, je ne
          répète pas tout ici, mais tu peux reprendre 1:1 le fichier que
          tu as déjà vu dans Emergent – c’est le même que plus haut. */}
    </div>
  );
}

export default App;
